var url = require('url');
var crypto = require('crypto');

var digest = function(config_path) {
	if (config_path) this.config = require(config_path);
	else this.config = require('./config/digest.key.json');	
	this.key;
	this.logger;
	this.maxage = 300000; // 5 min
	this.authorizedSources = new Array();
	return this;
}

// If the IP is authorized return true, else try to found the information necessary in headers
digest.prototype.getStatus = function (req, res) {
	this.logger.log("Digest", "getStatus", "source ip: " + req.connection.remoteAddress);
	if (this.IsIpAuthorized(req.connection.remoteAddress)) {
		this.logger.log("Digest", "getStatus", "Already authorized address");
		return true;
	} else {
		var headersToString ='';
		var nonce = req.headers["nonce"];
		var token = req.headers["token"];
		var sign = req.headers["sign"];
		this.logger.log("Digest", "getStatus", "headers: " + nonce + ", " + token + ", " + sign);
		if (nonce && token && sign) {
			// check nonce
			if (nonce < Math.round(new Date().getTime() - this.maxage) || nonce > Math.round(new Date().getTime())) {
				this.logger.log("Digest", "getStatus", "Invalid nonce. Nonce is : " + nonce + " Limit is : "+Math.round(new Date().getTime() - this.maxage));
				return false;
			}
			// check sign
			var hmac = crypto.createHmac('sha1', this.key);
			this.logger.log("Digest", "getStatus", "message : http://" + req.headers['host'] + req.url + ", " + nonce + ", " + token);
			hmac.update("http://" + req.headers['host'] + req.url + ", " + nonce + ", " + token);
			var digest = hmac.digest('base64');
			this.logger.log("Digest", "getStatus", "Expected signature = " + digest);
			if (sign == digest) {
				this.authorizedSources.push(req.connection.remoteAddress);
				return true;	
			} 
		} 
		return false;
	}
}

digest.prototype.rejectRequest = function(req, res, config) {
	this.logger.log("Digest", "rejectRequest", "url =>" + req.url + ", auth page =>" + config["authentication_page"]);
	if (req.url == "/" && config["authentication_page"]) {
		res.setHeader('Content-type', 'text/html');
		res.render(config["authentication_page"], {server_config: config});
		res.end();
		return true;
	} else {
		res.writeHead(401, {
			'nonce': new Date().getTime(), 
			'token': Math.round(Math.random()*10000000)
		});
		res.end();
		return false;
	}
}

digest.prototype.IsIpAuthorized = function (ip) {
	return require('./utils').inArray(this.authorizedSources, ip);
}

digest.prototype.setLogger = function(logger) {
	this.logger = logger;
}

var init = function(config_path, logger, callback) {
	var dig = new digest(config_path);
	dig.setLogger(logger);
	dig.logger.log("Digest", "init", "Initialising security");
	if (dig.config['key']) {
		dig.logger.log("Digest", "init", "Key specified. Loading it");
		dig.key = dig.config["key"];
	}
	if (dig.config['file']) { //using file if defined 
		dig.logger.log("Digest", "init", "file specified, trying to load it");
		var fs = require('fs');
		if (fs.existsSync( __dirname + dig.config['file'])) {
			fs.stat( __dirname + dig.config['file'], function(err, stats) {
				if (!err) {
					if (stats.isFile()) {
						fs.readFile( __dirname + dig.config['file'], function(err, data) {
							if (!err) {
								dig.key = data.toString('base64'); 
								return callback(dig);
							} else {
								dig.logger.log("Digest", "init", "Error accessing file. If a key is loaded then use it.");
								if (dig.key) return callback(dig);
								else dig.logger.log("Digest", "init", "no key specified. Stopping server");
							}
						});
					} else {
						dig.logger.log("Digest", "init", "File in configuration is not a standard file. If a key is loaded then use it.");
						if (dig.key)return callback(dig);
						else dig.logger.log("Digest", "init", "no key specified. Stopping server");
					}
				} else {
					dig.logger.log("Digest", "init", "Error accessing file. If a key is loaded then use it.");
					if (dig.key) return callback(dig);
					else dig.logger.log("Digest", "init", "no key specified. Stopping server");
				}
			});
		} else {
			dig.logger.log("Digest", "init", "File does not exists. If a key is loaded then use it.");
			if (dig.key) return callback(dig);
			else dig.logger.log("Digest", "init", "no file nor key specified. Stopping server");
		}
	} 
}

exports.init = init;
